import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './PokemonCard.css';

export class PokemonCard extends Component {
  static propTypes = {
    id: PropTypes.string,
    name: PropTypes.string,
    img: PropTypes.string,
    types: PropTypes.string
  };

  render() {
    const { pokemon } = this.props;
    const style = {
      backgroundImage: `url(${pokemon.sprites.front_default})`
    }
    console.log({ pokemon })
    return [
      <h2 className="pokemon__name" key={pokemon.name}>{pokemon.name}</h2>,
      <p className="pokemon__id" key={pokemon.id}>{pokemon.id}</p>,
      <div className="pokemon__img" key={style} style={style}></div>,
      <div className="pokemon__types" key={`hola--${pokemon.id}`}>
        {pokemon.types.map((elem, i) =>
          <span key={i}>{elem.type.name}</span>)
        }
      </div>
    ];
  }
}
