      <form className="search__form">
        <input
          type="text"
          className="input__search"
          placeholder="Search your favourite pokÃ©mon..."
          onChange={changeInput}
          onSubmit={submitForm}
        />
        <button className="button__search">Search</button>
      </form>
    );
  }
}
